<?php

namespace Gdr\GameBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * LibrarySectionRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class LibrarySectionRepository extends EntityRepository
{
    public function findSortedSections(){
        return $this->createQueryBuilder("ls")
            ->andWhere('ls.isMuseo = false')
            ->andWhere('ls.isArchivio = false')
            ->orderBy("ls.sort", "ASC")
            ->getQuery()->getResult();
    }

    public function findSortedSectionsMuseo(){
        return $this->createQueryBuilder("ls")
            ->andWhere('ls.isMuseo = true')
            ->orderBy("ls.sort", "ASC")
            ->getQuery()->getResult();
    }

    public function findSortedSectionsArchivio(){
        return $this->createQueryBuilder("ls")
            ->andWhere('ls.isArchivio = true')
            ->orderBy("ls.sort", "ASC")
            ->getQuery()->getResult();
    }
}
